import React from 'react';
import './VideoFooter.css';
import MusicNoteIcon from '@material-ui/icons/MusicNote';
import Ticker from 'react-ticker'


function VideoFooter() {
    return (
        <div className="videoFooter">
            <div className="videoFooter__text">
                <h3>@asifshaikh</h3>
                <p>this is a description</p>
                <MusicNoteIcon />
                <div class="videoFooter__ticker">
                        <Ticker mode="smooth">
                            {({ index }) => (
                            <>
                                <p className="ticker">Yoo whatup guys</p>

                            </>
                            )}
                        </Ticker>
                </div>

            </div>

            <img className="videoFooter__record" src="https://encrypted-vtbn3.gstatic.com/video?q=tbn:ANd9GcQ2i73gF6g3obG6BfpYrQGOJAbg7JTmpxU6nAK19OwE_O465TOx" />

        </div>
    )
}

export default VideoFooter
