import React, { useRef, useState} from 'react';
import VideoFooter from './VideoFooter';
import './Video.css';
import VideoSidebar from './VideoSidebar';


function Video() {
    const [play,setPlay]=useState(false);
    const videoRef = useRef(null);
    const onVideoPress =  () =>{
        if(play){
            videoRef.current.pause();
            setPlay(false);
        }
        else{
            videoRef.current.play();
            setPlay(true);
        }

    }
    return (
        <div className="video">
            <video 
                className="video__player" 
                loop
                onClick={onVideoPress}
                ref={videoRef}
                src="https://encrypted-vtbn3.gstatic.com/video?q=tbn:ANd9GcQ2i73gF6g3obG6BfpYrQGOJAbg7JTmpxU6nAK19OwE_O465TOx"  >

            </video>
              <VideoFooter />
            <VideoSidebar likes="500" shares="400" message="20"/>


        </div>
    )
}

export default Video
